import React, {useState} from "react";
import Zoom from '@material-ui/core/Zoom';
function CreateArea(props){

    const[note, setNote] = useState({
        title:"",
        content:""
    });

    function handleChange(event){
        const{name, value} = event.target;

        setNote(prevValue => {
            return{
                ...prevValue,
                [name]: value
            };
        });
    } 

    function handleClick(event){
        props.onClick(note);
        setNote({
            title :"",
            content :""
        });
        event.preventDefault();
    }

    const[expanded, setExpand] = useState(false);

    function expand(){
        setExpand(true);
    }



    return(
        <div>
        <form className="create-note">
            {expanded && <input onChange={handleChange} name="title" value={note.title} placeholder="Title" /> }
            <textarea onClick={expand} rows={expanded ? 3 : 1} onChange={handleChange} name="content" value={note.content} placeholder="Title Content" />
            <Zoom in={expanded}>
             <button onClick={handleClick} >Add</button> 
            </Zoom>
        </form>

        </div>

    )

}

export default CreateArea;
