import React, {useState} from "react";
import Header from "./header";
import Footer from "./footer";
import Note from "./note";
import CreateArea from "./createArea";

function App() {

    const[notes, setNotes] = useState([]);

    function onAdd(note){
        setNotes(preNotes => {
            return [...preNotes, note]
        });
        console.log(notes);
    }

    return (
        <div>
            <Header />
            <CreateArea 
                onClick = {onAdd}
            />
            <Note />
       
            <Footer />
        </div>
    )
};



export default App;
