import React from 'react'
import CharectersDetail from './CharectersDetail';

export const Characters = ({ isLoading, items }) => {
    return (
        isLoading ? <h1>Loading</h1> : (
            <section className="cards">
                { items.map((card) => (<CharectersDetail card={card} />)



                )}
            </section>
        )

    )
};
